INSERT INTO `cms_block` (`block_id`, `title`, `identifier`, `content`, `creation_time`, `update_time`, `is_active`) VALUES
(2, 'Landing page 1', 'landing_page1', '<div class="horizontal-section">\r\n\r\n	<a href="#">\r\n		<img src="{{media url="wysiwyg/infortis/ultimo/custom/landing/category01.jpg"}}" title="You can edit this block or replace it with custom content" alt="Sample banner" />\r\n    \r\n		<div class="caption dark2 full-width">\r\n			<h2 class="heading permanent">Category Banner</h2>\r\n			<p>Upload image and description for every category in the store</p>\r\n			<p>Create landing pages for categories</p>\r\n		</div>\r\n	</a>\r\n    \r\n</div>', '2012-10-04 14:41:08', '2014-07-02 10:43:52', 1),
(3, 'Custom footer links 2', 'block_footer_links2', '<ul class="links hide-below-768">\r\n\r\n	<li class="first">\r\n		<a href="{{store direct_url=''about-magento-demo-store/''}}" title="You can add more custom links here">About Us</a>\r\n	</li>\r\n	<li class="last">\r\n		<a href="{{store direct_url=''customer-service/''}}"  title="You can even replace these links with any other content">Customer Service</a>\r\n	</li>\r\n\r\n</ul>', '2012-10-04 14:41:08', '2013-12-16 20:39:29', 1),
(4, 'Custom footer links', 'block_footer_links', 'This block can replace Magento''s default footer links.\r\n\r\n<ul class="links">\r\n\r\n    <li class="first">\r\n    	<a href="{{store url=''about-magento-demo-store''}}" title="My custom link">Custom Link</a>\r\n    </li>\r\n    <li class=" last">\r\n    	<a href="{{store url=''about-magento-demo-store''}}" title="My sample link">Sample Link</a>\r\n    </li>\r\n    \r\n</ul>', '2012-10-04 14:41:09', '2013-12-02 19:43:29', 0),
(5, 'Category above collection', 'block_category_above_collection', '<div class="sample-block">Static CMS block displayed above list/grid of products. Put your own content here.</div>', '2012-10-04 14:41:09', '2012-10-22 10:40:05', 0),
(6, 'Footer primary bottom left - social links', 'block_footer_primary_bottom_left', '<div class="social-links ib-wrapper--square">\r\n	<a class="first" href="http://twitter.com/infortis" title="Follow Infortis on Twitter">\r\n		<span class="ib ib-hover ic ic-lg ic-twitter"></span>\r\n	</a>\r\n	<a href="#" title="Join us on Facebook">\r\n		<span class="ib ib-hover ic ic-lg ic-facebook"></span>\r\n	</a>\r\n	<a href="#" title="Join us on Google Plus">\r\n		<span class="ib ib-hover ic ic-lg ic-googleplus"></span>\r\n	</a>\r\n	<a href="#" title="Youtube">\r\n		<span class="ib ib-hover ic ic-lg ic-youtube"></span>\r\n	</a>\r\n	<a href="#" title="Vimeo">\r\n		<span class="ib ib-hover ic ic-lg ic-vimeo"></span>\r\n	</a>\r\n	<a href="#" title="Instagram">\r\n		<span class="ib ib-hover ic ic-lg ic-instagram"></span>\r\n	</a>\r\n	<a href="#" title="Pinterest">\r\n		<span class="ib ib-hover ic ic-lg ic-pinterest"></span>\r\n	</a>\r\n	<a href="#" title="Linked in">\r\n		<span class="ib ib-hover ic ic-lg ic-linkedin"></span>\r\n	</a>\r\n	<a href="#" title="VKontakte">\r\n		<span class="ib ib-hover ic ic-lg ic-vk"></span>\r\n	</a>\r\n	<a href="#" title="Renren">\r\n		<span class="ib ib-hover ic ic-lg ic-renren"></span>\r\n	</a>\r\n</div>', '2012-10-04 14:41:10', '2014-09-06 17:19:52', 1),
(7, 'Footer payment', 'block_footer_payment', '<img src="{{media url="wysiwyg/infortis/ultimo/custom/payment.gif}}" alt="Payment methods" title="Sample image with payment methods" />', '2012-10-04 14:41:10', '2012-10-04 14:41:10', 1),
(8, 'Category below collection', 'block_category_below_collection', '<div class="nested-container hide-below-768">\r\n\r\n    <div class="page-banners clearer">\r\n        <div class="grid12-6 banner">\r\n            <a href="{{store direct_url="about-magento-demo-store"}}" title="Replace this image with your own">\r\n                <img src="{{media url="wysiwyg/infortis/ultimo/custom/banners/01.png"}}" alt="Sample banner" />\r\n            </a>\r\n        </div>\r\n        <div class="grid12-6 banner">\r\n            <a href="{{store direct_url="about-magento-demo-store"}}" title="Replace this image with your own">\r\n                <img src="{{media url="wysiwyg/infortis/ultimo/custom/banners/03.png"}}" alt="Sample banner" />\r\n            </a>\r\n        </div>\r\n    </div>\r\n\r\n</div>', '2012-10-04 14:41:11', '2014-03-12 19:36:37', 0),
(9, 'Category above empty collection', 'block_category_above_empty_collection', '<div class="note-msg empty-catalog">\r\n\r\n	<h3>There are no products matching the selection</h3>\r\n	This is a custom CMS block displayed if category is empty.<br>\r\n	You can replace it with any custom content.\r\n\r\n</div>', '2012-10-04 14:41:11', '2013-04-07 06:06:44', 1),
(10, 'Product - secondary column bottom', 'block_product_secondary_bottom', '<div class="feature feature-icon-hover indent first">\r\n	<span class="ib ic ic-lg ic-plane"></span>\r\n	<p class="no-margin ">We will send this product in 2 days. <a href="#">Read more...</a></p>\r\n</div>\r\n<div class="feature feature-icon-hover indent">\r\n	<span class="ib ic ic-lg ic-phone"></span>\r\n	<p class="no-margin ">Call us now for more info about our products.</p>\r\n</div>\r\n<div class="feature feature-icon-hover indent">\r\n	<span class="ib ic ic-lg ic-reload"></span>\r\n	<p class="no-margin ">Return purchased items and get all your money back.</p>\r\n</div>\r\n<div class="feature feature-icon-hover indent last">\r\n	<span class="ib ic ic-lg ic-star"></span>\r\n	<p class="no-margin ">Buy this product and earn 10 special loyalty points!</p>\r\n</div>\r\n\r\n\r\n\r\n<!-- Social bookmarks from http://www.addthis.com/get/sharing  -->\r\n<!-- AddThis Button BEGIN -->\r\n<div class="feature-wrapper top-border margin-bottom">\r\n	<div class="addthis_toolbox addthis_default_style ">\r\n	<a class="addthis_button_preferred_1"></a>\r\n	<a class="addthis_button_preferred_2"></a>\r\n	<a class="addthis_button_preferred_3"></a>\r\n	<a class="addthis_button_preferred_4"></a>\r\n	<a class="addthis_button_compact"></a>\r\n	<a class="addthis_counter addthis_bubble_style"></a>\r\n	</div>\r\n	<script type="text/javascript" src="http://s7.addthis.com/js/300/addthis_widget.js#pubid=xa-5054e6c6502d114f"></script>\r\n</div>\r\n<!-- AddThis Button END -->\r\n<br/>\r\n', '2012-10-04 14:41:12', '2015-03-31 09:01:22', 1),
(11, 'Product - primary column bottom', 'block_product_primary_bottom', 'Sample content of the static block - primary column bottom.', '2012-10-04 14:41:12', '2013-09-02 20:15:07', 0),
(12, 'Cart - below totals', 'block_cart_below_totals', '<div class="hide-below-320" style="display: table; float: right; padding-right: 10px; text-align: right;"><span style="display: table-cell; vertical-align: middle;">Sample info about SSL Certificate</span> <img src="{{media url="wysiwyg/infortis/ultimo/custom/block_cart_below_totals.png"}}" alt="Example Image" title="Static CMS block displayed below totals. Put your own content here." /></div>', '2012-10-04 14:41:13', '2012-10-22 19:24:59', 1),
(13, 'One page checkout  below progress', 'block_opc_below_progress', '<div class="sample-block">Custom CMS block displayed below the one page checkout progress block. Put your own content here.</div>', '2012-10-04 14:41:14', '2013-01-16 21:49:12', 1),
(14, 'Mini cart above products', 'block_mini_cart_above_products', '<div style="padding:15px; background-color:#f5f5f5; color:#d90000;" title="Customizable CMS block for promo info">\r\n<a class="go" href="{{store url=""}}">Check our latest super promotions!</a>\r\n</div>', '2012-10-04 14:41:14', '2013-06-28 09:10:54', 0),
(15, 'Custom Tab 1', 'block_product_tab1', '<h2>Custom Static Block 1</h2>\r\n\r\n<p>Custom CMS block displayed as a tab. You can use it to display info about returns and refunds, latest promotions etc. You can put your own content here: text, HTML, images - whatever you like. There are <strong>many similar blocks</strong> accross the store. All CMS blocks are editable from the admin panel.</p>\r\n\r\n<div class="feature feature-icon-hover indent first">\r\n	<span class="ib ic ic-right ic-2x"></span>\r\n	<p class="no-margin"><strong>Magento Community Edition is the most powerful</strong> open source e-commerce software and can be downloaded 100% for free. Developers can modify the core code, add custom features and functionality by installing extensions from Magento Connect marketplace.</p>\r\n</div>\r\n<div class="feature feature-icon-hover indent">\r\n	<span class="ib ic ic-right ic-2x"></span>\r\n	<p class="no-margin"><strong>Manage the fully dynamic catalog</strong> with the intutive admistrative interface. The flexible catalog system includes various options for the display of items. Magento is also integrated with a variety of major payment gateways out of the box.</p>\r\n</div>\r\n<div class="feature feature-icon-hover indent last">\r\n	<span class="ib ic ic-right ic-2x"></span>\r\n	<p class="no-margin"><strong>Magento is a fully global platform</strong>, allowing for the expansion of business or simply offering multiple versions of your site to meet the needs of your customers.\r\nTranslated into over 60 languages, supporting multiple currencies, payment methods and taxes, Magento allows for internationalization of your online stores.</p>\r\n</div>', '2012-10-04 14:41:15', '2015-03-31 08:49:34', 1),
(16, 'Custom Tab 2', 'block_product_tab2', '<p>Another custom CMS block displayed as a tab. You can use it to display info about returns and refunds, latest promotions etc. You can put your own content here: text, HTML, images - whatever you like. There are <strong>many similar blocks</strong> accross the store. All CMS blocks are editable from the admin panel.</p>\r\n\r\n<p>Lorem ipsum dolor sit, consectetur adipiscing elit. Etiam neque velit, blandit sed scelerisque quis. Nullam ornare enim nec justo bibendum lobortis. In eget metus.</p>', '2012-10-04 14:41:15', '2015-03-31 08:49:12', 1),
(17, 'Cart - below table', 'block_cart_below_table', '<img src="{{media url="wysiwyg/infortis/ultimo/custom/banners/cart-discount.png"}}" alt="Magento discount code" title="Use discount code/coupon" />', '2012-10-04 14:41:16', '2014-01-11 12:21:58', 1),
